    61→const sanitizeInput = (str) => {
    62→  if (typeof str !== 'string') return '';
    63→  return str
    64→    .replace(/[<>]/g, '') // Remove < and > to prevent HTML injection
    65→    .trim()
    66→    .slice(0, 1000); // Limit length
    67→};
    68→
    69→// Email validation regex
    70→const isValidEmail = (email) => {
    71→  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    72→  return emailRegex.test(email);
    73→};
    74→
    75→app.post('/api/contact', limiter, async (req, res) => {
    76→  // Sanitize inputs
    77→  const name = sanitizeInput(req.body.name) || 'Unbekannt';
    78→  const email = sanitizeInput(req.body.email);
    79→  const message = sanitizeInput(req.body.message);
    80→
    81→  // Validate inputs
    82→  if (!email || !message) {
    83→    return res.status(400).json({ success: false, error: 'Email and message are required.' });
    84→  }
    85→
    86→  // Validate email format
    87→  if (!isValidEmail(email)) {
    88→    return res.status(400).json({ success: false, error: 'Invalid email format.' });
    89→  }
    90→
    91→  // Read existing queries from the JSON file
    92→  let queries = [];
    93→  try {
    94→    const data = fs.readFileSync(queriesFilePath, 'utf8');
    95→    queries = JSON.parse(data);
    96→  } catch (error) {
    97→    console.error('Error reading queries file:', error);
    98→    return res.status(500).json({ success: false, error: 'Unable to process the request.' });
    99→  }
   100→
   101→  // Add the new query
   102→  const newQuery = {
   103→    id: queries.length + 1, // Incremental ID
   104→    name,
   105→    email,
   106→    message,
   107→    timestamp: new Date().toISOString(),
   108→    response: {
   109→      subject: predefinedResponse.subject,
   110→      text: predefinedResponse.text.replace('{name}', name),
   111→      html: predefinedResponse.html.replace('{name}', name),
   112→    },
   113→  };
   114→  queries.push(newQuery);
   115→
   116→  // Save the updated queries back to the JSON file
   117→  try {
   118→    fs.writeFileSync(queriesFilePath, JSON.stringify(queries, null, 2));
   119→    res.json({ success: true, message: 'Your query has been recorded.' });
   120→  } catch (error) {
   121→    console.error('Error writing to queries file:', error);
   122→    res.status(500).json({ success: false, error: 'Unable to save your query.' });
   123→  }
   124→});
   125→
   126→// Serve the React app
   127→const buildPath = path.join(__dirname, 'dist');
   128→
   129→// Serve assets with long cache (files have version hashes)
   130→app.use('/assets', express.static(path.join(buildPath, 'assets'), {
   131→  maxAge: '1y',
   132→  immutable: true
   133→}));
   134→
   135→// Serve other static files with short cache
   136→app.use(express.static(buildPath, {
   137→  maxAge: '1h',
   138→  setHeaders: (res, filePath) => {
   139→    // Never cache index.html
   140→    if (filePath.endsWith('index.html')) {
   141→      res.setHeader('Cache-Control', 'no-cache, no-store, must-revalidate');
   142→    }
   143→  }
   144→}));
   145→
   146→// Fallback route for React SPA - no cache for HTML
   147→app.get('*', (req, res) => {
   148→  res.setHeader('Cache-Control', 'no-cache, no-store, must-revalidate');
   149→  res.sendFile(path.join(buildPath, 'index.html'));
   150→});
   151→
   152→// Start the server
   153→const PORT = process.env.PORT || 3000;
   154→app.listen(PORT, () => {
   155→  console.log(`Server running on port ${PORT}`);
   156→});
