     1→// Umami Analytics - GDPR-compliant, privacy-friendly analytics
     2→// Only loaded after explicit user consent
     3→
     4→const UMAMI_URL = 'https://analytics.strali.solutions/script.js';
     5→const WEBSITE_ID = 'a63e5fe3-c1ac-4d40-8e9a-fbfe4126ab0d';
     6→
     7→let umamiLoaded = false;
     8→
     9→/**
    10→ * Check if analytics consent has been given
    11→ * @returns {boolean}
    12→ */
    13→export const hasAnalyticsConsent = () => {
    14→  try {
    15→    const consent = localStorage.getItem('cookie-consent');
    16→    if (!consent) return false;
    17→    const parsed = JSON.parse(consent);
    18→    return parsed.analytics === true;
    19→  } catch {
    20→    return false;
    21→  }
    22→};
    23→
    24→/**
    25→ * Load Umami analytics script dynamically
    26→ * Only loads if consent is given and script hasn't been loaded yet
    27→ * @returns {boolean} Whether the script was loaded
    28→ */
    29→export const loadUmami = () => {
    30→  // Don't load if already loaded
    31→  if (umamiLoaded) {
    32→    return false;
    33→  }
    34→
    35→  // Don't load if no consent
    36→  if (!hasAnalyticsConsent()) {
    37→    return false;
    38→  }
    39→
    40→  // Check if script already exists in DOM
    41→  if (document.querySelector(`script[src="${UMAMI_URL}"]`)) {
    42→    umamiLoaded = true;
    43→    return false;
    44→  }
    45→
    46→  // Create and inject the script
    47→  const script = document.createElement('script');
    48→  script.src = UMAMI_URL;
    49→  script.async = true;
    50→  script.defer = true;
    51→  script.setAttribute('data-website-id', WEBSITE_ID);
    52→
    53→  script.onload = () => {
    54→    umamiLoaded = true;
    55→  };
    56→
    57→  script.onerror = () => {
    58→    console.warn('Failed to load Umami analytics');
    59→  };
    60→
    61→  document.head.appendChild(script);
    62→  return true;
    63→};
    64→
    65→/**
    66→ * Initialize analytics on app load if consent exists
    67→ */
    68→export const initAnalytics = () => {
    69→  if (hasAnalyticsConsent()) {
    70→    loadUmami();
    71→  }
    72→};
    73→
    74→/**
    75→ * Disable Umami analytics
    76→ * Removes the script and clears tracking state
    77→ */
    78→export const disableUmami = () => {
    79→  // Remove the Umami script from DOM
    80→  const script = document.querySelector(`script[src="${UMAMI_URL}"]`);
    81→  if (script) {
    82→    script.remove();
    83→  }
    84→
    85→  // Clear the global umami object to stop any tracking
    86→  if (window.umami) {
    87→    delete window.umami;
    88→  }
    89→
    90→  // Reset loaded state
    91→  umamiLoaded = false;
    92→};
