    48→  script.src = UMAMI_URL;
    49→  script.async = true;
    50→  script.defer = true;
    51→  script.setAttribute('data-website-id', WEBSITE_ID);
    52→
    53→  script.onload = () => {
    54→    umamiLoaded = true;
    55→  };
    56→
    57→  script.onerror = () => {
    58→    console.warn('Failed to load Umami analytics');
    59→  };
    60→
    61→  document.head.appendChild(script);
    62→  return true;
    63→};
    64→
    65→/**
    66→ * Initialize analytics on app load if consent exists
    67→ */
    68→export const initAnalytics = () => {
    69→  if (hasAnalyticsConsent()) {
    70→    loadUmami();
    71→  }
    72→};
    73→
    74→/**
    75→ * Disable Umami analytics
    76→ * Removes the script and clears tracking state
    77→ */
    78→export const disableUmami = () => {
    79→  // Remove the Umami script from DOM
    80→  const script = document.querySelector(`script[src="${UMAMI_URL}"]`);
    81→  if (script) {
    82→    script.remove();
    83→  }
    84→
    85→  // Clear the global umami object to stop any tracking
    86→  if (window.umami) {
    87→    delete window.umami;
    88→  }
    89→
    90→  // Reset loaded state
    91→  umamiLoaded = false;
    92→};
